import { useState, useEffect } from "react";
import { Drawer, Avatar, List, Typography, Image } from "antd";

import style from './DrawerDiscussionDetail.module.scss';

const { Text } = Typography;

const DrawerCreateDiscussion = ({ discussion, open, onCloseDrawer }) => {
  const [mobile, setMobile] = useState(window.innerWidth <= 768)
  useEffect(() => {
    const changeOnMobile = () => setMobile(window.innerWidth <= 768);
    window.addEventListener("resize", changeOnMobile);
    return () => {
      window.removeEventListener("resize", changeOnMobile);
    }
  }, []);

  const imagePlaceholder = "data:image/png;base64,";

  return (
    <Drawer
      title="Discussion Detail"
      placement="left"
      width={`${mobile ? '100%' : '410px' }`}
      onClose={onCloseDrawer}
      visible={open}>
      <div className={style.container}>
        <div className={style.imageWrapper}>
          <Image
            width={200}
            height={200}
            src={discussion.imageUrl || 'error'}
            fallback={imagePlaceholder}
          />
        </div>
        <div className={style.contentWrapper}>
          <div className={style.label}>Name</div>
          <div className={style.content}>{discussion.name}</div>
        </div>
        <div className={style.contentWrapper}>
          <div className={style.label}>Code</div>
          <div className={style.content}>{discussion.code}</div>
        </div>
        <div className={style.contentWrapper}>
          <div className={style.label}>Description</div>
          <div className={style.description}>{discussion.description}</div>
        </div>
        <div className={style.contentWrapper}>
          <div className={style.label}>Members</div>
          <div>
            <List
              itemLayout="horizontal"
              dataSource={discussion.members}
              renderItem={member => (
                <List.Item
                  key={member.id}
                  className={style.memberListWrapper}>
                  <List.Item.Meta
                    className={style.memberListMeta}
                    avatar={
                      member.imageUrl
                        ? <Avatar src={member.imageUrl} size={40} />
                        : <Avatar size={40}>{member.name.charAt(0)}</Avatar>
                    }
                    title={<Text ellipsis className={style.main}>{member.name}</Text>}
                    description={<Text ellipsis className={style.secondary}>{member.email}</Text>}
                  />
                </List.Item>
              )}
            />
          </div>
        </div>
      </div>
    </Drawer>
  );
}

export default DrawerCreateDiscussion;
